$(document).ready(function() {
	$(".button_vdf_toggle_filter").click(function() {
		$(".vdf_search_filters_column").addClass("on");
		$("body, .homepage_wrap").addClass("on");
		setTimeout(function() {
			$(".filter-result-inner").scrollTop(0)
		}, 1e3)
	});
	$(".button_close_filter, #applyFilterMobile").click(function() {
		$(".vdf_search_filters_column").removeClass("on");
		$("body, .homepage_wrap").removeClass("on")
	});
	$(window).width() < 1060 && $("#btnSearch").click(function() {
		$(".vdf_search_filters_column").removeClass("on");
		$("body, .homepage_wrap").removeClass("on")
	});
	$(window).width() < 768 ? $(".filter-result-inner").css("height", $(window).height() - 85) : $(window).width() < 1024 && $(".filter-result-inner").css("height", $(window).height() - 110);
	$(document).mouseup(function(n) {
		var t = $(".vdf_search_filters_column, .ui-autocomplete, .ui-datepicker");
		$(window).width() < 1060 && (t.is(n.target) || t.has(n.target).length !== 0 || t.hasClass("on") && ($(".vdf_search_filters_column").removeClass("on"), $("body, .homepage_wrap").removeClass("on")))
	})
});